import React, {useState} from "react";
import {TOTAL_SCREENS, GET_SCREEN_INDEX} from '../../../utilities/commonUtils'
import ScrollService from '../../../utilities/ScrollService'
import{faBars} from '@fontawesome/free-solid-svg-icons'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import './Header.css' 

export default function Header () {
    const [selectedScreen, setSelectedScreen] = useState(0)
    const [showHeaderOptions, setShowHeaderOptions] = useState(false)

    const updateCurrentScreen = (currentScreen)=>{
        if(!currentScreen || !currentScreen.screenInView) 
        return;
        let screenIndex = GET_SCREEN_INDEX(currentSreen.screenInView)
        if(screenIndex < 0)
        return
    }
    let currentScreenSubscription = ScrollService.currentScreenBroadCaster.subscribe(updateCurrentScreen)
    
    const getHeaderOptions = ()=>{
        return (
            TOTAL_SCREENS.map((screen, i)=>(
                <div key={screen.screen_name} className={getOptionsClass(i)}
                onClick={() => switchScreen(i, screeb)}>
                    <span>{screen.screen_name}</span>
                </div>
            ))
        )
    }
    const getHeaderOptionsClass =(index)=>{
        let classes = "header-options";
        if (index < TOTAL_SCREENS.length -1)
        classes += "header-option-seperator";
        return
    }

    const switchScreen = (index, screen) =>{
        let screenComponent = document.getElementById(screen)
        if(!screenComponent)
        return
        screenComponent.scrollIntoView({behavior: 'smooth'});
        setSelectedScreen(index);setShowHeaderOptions(false);
    };

    return (
        <div>
                <div className = "header-options" onClick={()=>setShowHeaderOptions(!showHeaderOptions)}>
                    <div className='header-parent'>
                        <div className="header-hamburger" onClick={() =>setShowHeaderOptions(!showHeaderOptions)}>

                        <FontAwesomeIcon className='header-hamburger-bars' icon={faBars} />
                    </div>
                    <div className="header-logo">
                        <span>KAI~</span>
                    </div>
                    <div className={(showHeaderOptions)?"header-options show-hamburger-options": "header-options"}>
                        {getHeaderOptions()}
                    </div>
                </div>
        </div>
    </div>
    )
}
